// src/components/PerformanceMetricsComponent.js
import React, { useState } from 'react';

function PerformanceMetricsComponent() {
  const [tasksCompleted, setTasksCompleted] = useState(0);

  const handleCompleteTask = () => {
    setTasksCompleted(tasksCompleted + 1);
  };

  return (
    <div>
      <h2>Performance Metrics Component</h2>
      <button onClick={handleCompleteTask}>Complete Task</button>
      <p>Tasks Completed: {tasksCompleted}</p>
    </div>
  );
}

export default PerformanceMetricsComponent;
