// src/components/TimeTrackingModule.js
import React, { useState } from 'react';

function TimeTrackingModule() {
  const [timeEntries, setTimeEntries] = useState({});
  const [employeeId, setEmployeeId] = useState('');

  const logStartTime = () => {
    if (employeeId) {
      setTimeEntries((prevEntries) => ({
        ...prevEntries,
        [employeeId]: { start: new Date(), end: null },
      }));
      alert(`Start time logged for employee ${employeeId}`);
    }
  };

  const logEndTime = () => {
    if (employeeId && timeEntries[employeeId]) {
      setTimeEntries((prevEntries) => ({
        ...prevEntries,
        [employeeId]: { ...prevEntries[employeeId], end: new Date() },
      }));
      alert(`End time logged for employee ${employeeId}`);
    } else {
      alert('No start time found for this employee');
    }
  };

  const calculateHours = (entry) => {
    if (entry.end) {
      const diff = entry.end - entry.start;
      return diff / (1000 * 60 * 60); // Convert milliseconds to hours
    }
    return null;
  };

  return (
    <div>
      <h2>Time Tracking Module</h2>
      <input
        type="text"
        value={employeeId}
        onChange={(e) => setEmployeeId(e.target.value)}
        placeholder="Enter Employee ID"
      />
      <button onClick={logStartTime}>Log Start Time</button>
      <button onClick={logEndTime}>Log End Time</button>
      <ul>
        {Object.entries(timeEntries).map(([id, entry]) => (
          <li key={id}>
            Employee {id}: Hours Worked - {calculateHours(entry) || 'N/A'}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TimeTrackingModule;
