// src/components/RoleBasedAccessComponent.js
import React, { useState } from 'react';

function RoleBasedAccessComponent() {
  const [roles, setRoles] = useState({
    admin: ['view_sales', 'edit_inventory'],
    manager: ['view_sales'],
    employee: [],
  });

  const [userRole, setUserRole] = useState('employee');
  const [permissionToCheck, setPermissionToCheck] = useState('');

  const handleRoleChange = (event) => {
    setUserRole(event.target.value);
  };

  const checkPermission = () => {
    const userPermissions = roles[userRole] || [];
    if (userPermissions.includes(permissionToCheck)) {
      alert(`User has permission: ${permissionToCheck}`);
    } else {
      alert(`User does not have permission: ${permissionToCheck}`);
    }
  };

  return (
    <div>
      <h2>Role-Based Access Component</h2>
      <label>
        Select User Role:
        <select value={userRole} onChange={handleRoleChange}>
          <option value="admin">Admin</option>
          <option value="manager">Manager</option>
          <option value="employee">Employee</option>
        </select>
      </label>
      <br />
      <input
        type="text"
        placeholder="Enter Permission"
        value={permissionToCheck}
        onChange={(e) => setPermissionToCheck(e.target.value)}
      />
      <button onClick={checkPermission}>Check Permission</button>
    </div>
  );
}

export default RoleBasedAccessComponent;
