// src/components/RoleBasedAccessModule.js
import React, { useState } from 'react';

function RoleBasedAccessModule() {
  const [roles, setRoles] = useState({});
  const [userRoles, setUserRoles] = useState({});
  const [roleId, setRoleId] = useState('');
  const [permissions, setPermissions] = useState('');
  const [userId, setUserId] = useState('');
  const [permissionToCheck, setPermissionToCheck] = useState('');

  const addRole = () => {
    if (roleId && permissions) {
      setRoles((prevRoles) => ({
        ...prevRoles,
        [roleId]: permissions.split(',').map(p => p.trim()),
      }));
      alert(`Role ${roleId} added`);
    }
  };

  const assignRole = () => {
    if (userId && roles[roleId]) {
      setUserRoles((prevUserRoles) => ({
        ...prevUserRoles,
        [userId]: roleId,
      }));
      alert(`Role ${roleId} assigned to user ${userId}`);
    } else {
      alert('Role does not exist');
    }
  };

  const checkAccess = () => {
    const role = roles[userRoles[userId]];
    if (role && role.includes(permissionToCheck)) {
      alert(`User ${userId} has permission ${permissionToCheck}`);
    } else {
      alert(`User ${userId} does not have permission ${permissionToCheck}`);
    }
  };

  return (
    <div>
      <h2>Role-Based Access Module</h2>
      <input
        type="text"
        value={roleId}
        onChange={(e) => setRoleId(e.target.value)}
        placeholder="Enter Role ID"
      />
      <input
        type="text"
        value={permissions}
        onChange={(e) => setPermissions(e.target.value)}
        placeholder="Enter Permissions (comma-separated)"
      />
      <button onClick={addRole}>Add Role</button>

      <input
        type="text"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Enter User ID"
      />
      <input
        type="text"
        value={roleId}
        onChange={(e) => setRoleId(e.target.value)}
        placeholder="Enter Role ID"
      />
      <button onClick={assignRole}>Assign Role</button>

      <input
        type="text"
        value={userId}
        onChange={(e) => setUserId(e.target.value)}
        placeholder="Enter User ID"
      />
      <input
        type="text"
        value={permissionToCheck}
        onChange={(e) => setPermissionToCheck(e.target.value)}
        placeholder="Enter Permission to Check"
      />
      <button onClick={checkAccess}>Check Access</button>
    </div>
  );
}

export default RoleBasedAccessModule;
